import { all, fork, put, delay, takeLatest } from "@redux-saga/core/effects";
import axios from 'axios';
import {
    // FOLLOW_FAILURE,
    // FOLLOW_REQUEST,
    // FOLLOW_SUCCESS,
    LOG_IN_FAILURE,
    LOG_IN_REQUEST,
    LOG_IN_SUCCESS,
    LOG_OUT_FAILURE,
    LOG_OUT_REQUEST,
    LOG_OUT_SUCCESS,
    SIGN_UP_FAILURE,
    SIGN_UP_REQUEST,
    SIGN_UP_SUCCESS,
    // UNFOLLOW_FAILURE,
    // UNFOLLOW_REQUEST,
    // UNFOLLOW_SUCCESS,
  } from '../reducers/user';

function loginAPI(data) {
    return axios.post('/api/login', data)
}

function* logIn(action) {
    try {
        yield delay(1000);
        // const result = yield call(loginAPI, action.data);    //call을 쓰면 동기, fork를 쓰면 비동기. result값을 받고 진행해야 하므로 call을 사용.
        yield put({
            type: LOG_IN_SUCCESS,
            data: result.data
        });
    } catch (err) {
        yield put({
            type: LOG_IN_FAILURE,
            error: err.response.data
        })
    }    
}

function logoutAPI() {
    return axios.post('/api/logout')
}

function* logOut() {
    try {
        yield delay(1000);
        // const result = yield call(logoutAPI);    //call을 쓰면 동기, fork를 쓰면 비동기. result값을 받고 진행해야 하므로 call을 사용.
        yield put({
            type: LOG_OUT_SUCCESS,
        });
    } catch (err) {
        yield put({
            type: LOG_OUT_FAILURE,
            error: err.response.data
        })
    }    
}

function signUpAPI() {
    return axios.post('/api/logout')
}

function* signUp() {
    try {
        yield delay(1000);        
        yield put({
            type: SIGN_UP_SUCCESS,
        });
    } catch (err) {
        yield put({
            type: SIGN_UP_FAILURE,
            error: err.response.data
        })
    }    
}

function* watchLogin() {
    yield takeLatest(LOG_IN_REQUEST, logIn);
}

function* watchLogOut() {
    yield takeLatest(LOG_OUT_REQUEST, logOut);
}

function* watchSignUp() {
    yield takeLatest(SIGN_UP_REQUEST, signUp);
}

export default function* userSaga() {
    yield all([
        fork(watchLogin),
        fork(watchLogOut),
        fork(watchSignUp)
    ]);
}
